package com.recio.aitor;

public class PT12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 25;
		num+=5;
		System.out.println("Suma: " + num);
		
		num-=15;
		System.out.println("Resta: " + num);
		
		num*=2;
		System.out.println("Multiplicación: " + num);
		
		num/=2;
		System.out.println("División: " + num);
		
		num%=3;
		System.out.println("Cociente: " + num);
	}

}
